const initialState = {
    status: "All",
    todoList: [
        { id: 1, name: "hello", complete: false },
        { id: 2, name: "hello1", complete: false },
    ],
};

const rootReducer = (state, action) => {
    switch (action.type) {
        case "STATUS": {
            return {
                ...state,
                status: action.payload,
            };
        }

        default: {
            return state;
        }
    }
};

export default rootReducer;
