const initialState = {
    status: "All",

    inputText: "",
    todoList: [{ id: 1, name: "hello", complete: true }],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "STATUS": {
            return {
                ...state,
                status: action.payload,
            };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
