const initialState = {
    inputText: "",
    todoList: [],
    checkBox: "false",
    active: "All",
    complete: "false",
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "CHECKBOX": {
            let id = state.todoList.map((todo, id) => {
                return id === action.id ? true : false;
            });

            console.log("id", id);
            return {
                ...state,
                checkBox: action.payload,
            };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
