const initialState = {
    status: "All",

    inputText: "",
    todoList: [],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "STATUS": {
            console.log(action.payload);
            return {
                ...state,
                status: action.payload,
            };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
