import { useState } from "react";
import { useSelector } from "react-redux";
import footer from "./footer.css";

const Footer = () => {
    const todoList = useSelector((state) => state.todoList);
    const [status, setStatus] = useState("All");
    const buttonBtn = [
        {
            status: "All",
            isComplete: false,
        },
        {
            status: "Active",
            isComplete: false,
        },
        {
            status: "Complete",
            isComplete: false,
        },
    ];
    return (
        <div className="footer">
            <span className="footerAmount">{todoList.length} item left</span>
            <div className="footerBtn">
                {buttonBtn.map((btn) => (
                    <button className="btn">{btn.status}</button>
                ))}
            </div>
            {/* <div className="footerButton">
                <button className="btn active" value="All">
                    All
                </button>
                <button className="btn" value="Active">
                    Active
                </button>
                <button className="btn" value="Complete">
                    Completed
                </button>
            </div> */}
        </div>
    );
};

export default Footer;
