const initialState = {
    inputText: "",
    todoList: [],
    checkBox: "false",
    active: "All",
    complete: "false",
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "CHECKBOX": {
            const checked = state.todoList.map((todo) => {
                return todo.id === action.payload ? true : false;
            });
            return {
                ...state,
                checkBox: checked,
            };
        }
        case "CHECKDOWN": {
            return {
                ...state,
                checkBox: false,
            };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
