const initialState = {
    status: "All",

    inputText: "",
    todoList: [
        { id: 1, name: "hello", complete: false },
        { id: 2, name: "hello1", complete: true },
    ],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "STATUS": {
            return {
                ...state,
                status: action.payload,
            };
        }
        case "COMPLETE": {
            console.log("action", action.payload);
            return {
                ...state,
                todoList: [
                    ...state.todoList,
                    (state.todoList.complete = action.payload),
                ],
            };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
