export const addTodo = (text) => {
    return {
        type: "ADD_TODO",
        payload: text,
    };
};

export const statusTodo = (checkbox) => {
    return {
        type: "STATUS",
        payload: checkbox,
    };
};
export const completeTodo = (id) => {
    return {
        type: "COMPLETE",
        payload: id,
    };
};
