import { act } from "react-dom/test-utils";

const initialState = {
    inputText: "",
    todoList: [],
    checkBox: false,
    active: "All",
    complete: false,
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return {
                ...state,
                todoList: [...state.todoList, action.payload],
            };
        }
        case "CHECKBOX": {
            const check = action.payload.check;
            console.log(check);
            return {
                ...state,
                checkBox: check,
                complete: check,
            };
        }
        case "CHECKDOWN": {
            // const checked = state.todoList.map
            // return {
            //     ...state,
            //     checkBox:
            // };
        }
        default: {
            return state;
        }
    }
};

export default rootReducer;
